print('Minha toalha é do Roblox :)')

#Criando toalha

class Towel:
    def __init__ (self, color: str, size: str): #consturctor
        self.color = color
        self.size = size               #atribute
        self.wetness = 0              #atribute

    def __str__ (self) -> str:
        return f"Cor: {self.color}\nTam: {self.size}\nUmi: {self.wetness}"

    def getMaxWetness (self) -> int:
        if self.size == "P":
            return 10
        if self.size == "M":
            return 20
        if self.size == "G":
            return 30
        return 0

    def dry (self, amount: int) -> None:
        self.wetness += amount
        if (self.wetness >= self.getMaxWetness()):
            print("A toalha está encharcada")
            self.wetness = self.getMaxWetness()

    def isDry (self) -> bool:
        return self.wetness == 0

    def wrignOut (self) -> None:
        self.wetness = 0

def main():
    towel: Towel = Towel("", "")
    while True:
        commandLine: str = input()
        args: list[str] = commandLine.split(" ")
        if args[0] == "quit":
            break
        else:
            print(f"Erro: \" {args[0]} \" não encontrado")

main()
